css-template.body:
html, body {
	font-family: Arial,Helvetica,sans-serif;
	height: 100%;
	padding: 0;
	margin: 0;
}

css-template.default-classes:
.bg_color {
	background-color: __bg-color__;
}

div#preloaded-images {
	position: absolute;
	overflow: hidden;
	left: -9999px;
	top: -9999px;
	height: 1px;
	width: 1px;
}

css-template.banner-wrapper:
#banner-wrapper {
	box-sizing: content-box;
	overflow: hidden;
	position: relative;
	width: __width__px;
	height: __height__px;
	border: 1px __border-color__ solid;
}

css-template.top-clicker:
#clicker {
	position: absolute;
	width: 100%;
	height: 100%;
	top: 0;
	left: 0;
	cursor: pointer;
	opacity: 100;
	z-index: __top-layer-zindex__;
}

js-template.setup-top:
if (Enabler.isInitialized()) {
	enablerInitHandler();
} else {
	Enabler.addEventListener(studio.events.StudioEvent.INIT, enablerInitHandler);
}

function enablerInitHandler() {
	"use strict";
	// If true, start function. If false, listen for PAGE_LOADED.
	if (Enabler.isPageLoaded()) {
		pageLoadedHandler();
	} else {
		Enabler.addEventListener(studio.events.StudioEvent.PAGE_LOADED, pageLoadedHandler);
	}
}

